configfiles.configfile:
## Load library
library(minfi, quietly=TRUE, warn.conflicts=FALSE,verbose = FALSE)
## Load the RGset data if(!is.null("${RGset}")){ load("${RGset}") }
## Set phenotype data pd = pData(RGset) xlab=as.character("${xlabel}")
## Produce PDF file if (!is.null("${pdffile}")) { ## Make PDF of density plot pdf("${pdffile}") minfi::densityPlot(dat=RGset,sampGroups=pd\$status,main='Density Plot',xlab=xlab) dev.off() }
