configfiles.configfile:
### code chunk: Load all required libraries quietly library('minfi', quietly=TRUE, warn.conflicts=FALSE,verbose = FALSE)
## Load the RGset data if(!is.null("${RGset}")){ load("${RGset}") }
## Set phenotype data pd = pData(RGset) files = gsub(".+/","",pd\$filenames) maintitle=as.character("${mainTitle}") numPositions=as.integer("${numPositions}")
## Produce PDF file if (!is.null("${pdffile}")) { ## Make PDF of density plot pdf("${pdffile}") minfi::mdsPlot(dat=RGset,sampNames=files,sampGroups=pd\$status,main=maintitle,numPositions=numPositions,pch=19) dev.off() }
