configfiles.configfile:
### code chunk: Load all required libraries quietly library(minfi, quietly=TRUE, warn.conflicts=FALSE,verbose = FALSE)
## Load the RGset data if(!is.null("${RGset}")){ load("${RGset}") }
## Set phenotype data pd = pData(RGset) files = gsub(".+/","",pd\$filenames)
## Produce PDF file if (!is.null("${pdffile}")) { # Make PDF of QC report minfi::qcReport(rgSet=RGset,sampNames=files,sampGroups=pd\$status,pdf="${pdffile}") }
